import Footer from '../Components/Footer';
import Structure from './Structure';
import {useState , useEffect} from 'react';
function ShowPage()
{
    const [loadStatus,setLoadStatus]=useState(true);
    const[update,setUpdate]=useState([]);
    const[show,setShow]=useState(null);
    useEffect(()=>
    {
        fetch('https://meeting-react-a-default-rtdb.firebaseio.com/meet_detail.json').then
        (response=>response.json()).then(data=>
            {
                const meetings=[];
                for(let key in data)
                {
                    const meet_detail={
                        id:key,
                        ...data[key]                    
                }
                meetings.push(meet_detail);
                setLoadStatus(false);
                setUpdate(meetings);
                let len=update.length;
                if(update.length < 1)
                {
                    setShow("No meeting has been scheduled yet");
                    console.log(show);
                }
            }
    })
    },[])
    return(
        <div>
            <h1 className='meet-schedule'>Your Meeting Schedule </h1>
            {/* animation gif */}
            <div class={loadStatus?'center':'d-none'}>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
                <div class="wave"></div>
            </div>
            {/* end gif */}
            <h2 className='show-msg'>{show}</h2>
            <div>
                {
                    update.map((meet)=>
                    {
                        return <Structure title={meet.title} date={meet.date_time} link={meet.link} desc={meet.description} />
                    })
                }
            </div>
            <Footer/>
        </div>
    );
}

export default ShowPage;
